// src/pages/admin/ManageBooks.jsx
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { ref, get, update, remove, push } from "firebase/database";
import { db } from "../../firebase";
import toast, { Toaster } from "react-hot-toast";
import { motion, AnimatePresence } from "framer-motion";
import { ClipLoader } from "react-spinners";
import { uploadToCloudinary } from "../../services/cloudinary";
import { FiEdit, FiSave, FiTrash2, FiPlus, FiX } from "react-icons/fi";

export default function ManageBooks() {
    const navigate = useNavigate();
    const [books, setBooks] = useState([]);
    const [filtered, setFiltered] = useState([]);
    const [search, setSearch] = useState("");
    const [loading, setLoading] = useState(true);
    const [showAddForm, setShowAddForm] = useState(false);
    const [uploading, setUploading] = useState(false);
    const [editingBook, setEditingBook] = useState(null); // لتعديل كتاب موجود
    const [imageFiles, setImageFiles] = useState([]);
    const [imagePreviews, setImagePreviews] = useState([]);

    const FIXED_CATEGORIES = ["تاريخ وسياسة", "فلسفة وعلم نفس", "ادب", "شعر ومسرح"];

    const [newBook, setNewBook] = useState({
        title: "", author: "", transl: "", type: "عربي",
        category: "", price: "", HPaper: "", description: "", status: "available",
    });

    useEffect(() => {
        const isAdmin = localStorage.getItem("isAdmin") || sessionStorage.getItem("isAdmin");
        if (!isAdmin) navigate("/admin/login");
    }, [navigate]);

    useEffect(() => {
        fetchBooksData();
    }, []);

    const fetchBooksData = async () => {
        setLoading(true);
        try {
            const snap = await get(ref(db, "books"));
            if (snap.exists()) {
                const data = Object.entries(snap.val()).map(([key, value]) => ({ key, ...value }));
                const sorted = data.sort((a, b) => (b.createdAt || 0) - (a.createdAt || 0));
                setBooks(sorted);
                setFiltered(sorted);
            } else {
                setBooks([]);
                setFiltered([]);
            }
        } catch (err) {
            toast.error("فشل تحميل الكتب");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        const term = search.toLowerCase();
        setFiltered(books.filter(b =>
            b.title?.toLowerCase().includes(term) ||
            b.author?.toLowerCase().includes(term)
        ));
    }, [search, books]);

    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        setImageFiles(files);
        const previews = files.map(file => URL.createObjectURL(file));
        setImagePreviews(previews);
    };

    const handleRemoveImage = (index) => {
        if (editingBook) {
            const newImages = [...editingBook.images];
            newImages.splice(index, 1);
            setEditingBook({ ...editingBook, images: newImages });
        } else {
            const newFiles = [...imageFiles];
            newFiles.splice(index, 1);
            setImageFiles(newFiles);
            const newPreviews = [...imagePreviews];
            newPreviews.splice(index, 1);
            setImagePreviews(newPreviews);
        }
    };

    const handleAddBook = async (e) => {
        e.preventDefault();
        if (!newBook.title || !newBook.author || !newBook.price || (imageFiles.length === 0 && !editingBook)) {
            toast.error("املأ جميع الحقول المطلوبة واختر صورة واحدة على الأقل");
            return;
        }

        setUploading(true);
        try {
            let imageUrls = editingBook ? [...(editingBook.images || [])] : [];
            if (imageFiles.length > 0) {
                const newUrls = await uploadToCloudinary(imageFiles);
                imageUrls = [...imageUrls, ...newUrls];
            }

            const bookData = {
                ... (editingBook ? editingBook : newBook),
                ...newBook,
                price: Number(newBook.price),
                images: imageUrls,
                createdAt: editingBook ? editingBook.createdAt : Date.now(),
            };

            const booksRef = ref(db, "books");
            const bookRef = editingBook ? ref(db, `books/${editingBook.key}`) : push(booksRef);
            await update(bookRef, bookData);

            toast.success(editingBook ? "تم تعديل الكتاب بنجاح!" : "تم إضافة الكتاب بنجاح!");
            resetForm();
            setShowAddForm(false);
            setEditingBook(null);
            fetchBooksData();
        } catch (error) {
            console.error("Error:", error);
            toast.error("فشل العملية");
        } finally {
            setUploading(false);
        }
    };

    const resetForm = () => {
        setNewBook({
            title: "", author: "", transl: "", type: "عربي",
            category: "", price: "", HPaper: "", description: "", status: "available",
        });
        setImageFiles([]);
        setImagePreviews([]);
    };

    const startEditing = (book) => {
        setNewBook({
            title: book.title || "", author: book.author || "", transl: book.transl || "",
            type: book.type || "عربي", category: book.category || "", price: book.price || "",
            HPaper: book.HPaper || "", description: book.description || "", status: book.status || "available",
        });
        setEditingBook(book);
        setImageFiles([]);
        setImagePreviews([]);
        setShowAddForm(true);
    };

    const handleChange = (field, value) => {
        setNewBook(prev => ({ ...prev, [field]: value }));
    };

    const handleDelete = async (book) => {
        if (!window.confirm(`هل أنت متأكد من حذف "${book.title}"؟`)) return;
        try {
            await remove(ref(db, `books/${book.key}`));
            toast.success("تم الحذف");
            fetchBooksData();
        } catch {
            toast.error("فشل الحذف");
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("isAdmin");
        sessionStorage.removeItem("isAdmin");
        navigate("/admin/login");
    };

    return (
        <div className="min-h-screen bg-gray-50 p-6" dir="rtl">
            <Toaster position="top-center" />
            <div className="max-w-7xl mx-auto">
                <div className="flex justify-between items-center mb-8">
                    <h1 className="text-3xl font-bold text-gray-800">لوحة تحكم المكتبة</h1>
                    <button onClick={handleLogout} className="bg-red-500 hover:bg-red-600 text-white px-6 py-2 rounded-lg font-medium">
                        تسجيل الخروج
                    </button>
                </div>

                <button
                    onClick={() => { setShowAddForm(true); setEditingBook(null); resetForm(); }}
                    className="mb-6 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-medium"
                >
                    إضافة كتاب جديد
                </button>

                <AnimatePresence>
                    {showAddForm && (
                        <motion.div
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            className="bg-white p-8 rounded-xl shadow-lg mb-8"
                        >
                            <h2 className="text-2xl font-bold mb-6 text-gray-800">
                                {editingBook ? "تعديل الكتاب" : "إضافة كتاب جديد"}
                            </h2>
                            <form onSubmit={handleAddBook} className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <input
                                    type="text"
                                    placeholder="عنوان الكتاب *"
                                    value={newBook.title}
                                    onChange={(e) => handleChange("title", e.target.value)}
                                    required
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                />
                                <input
                                    type="text"
                                    placeholder="اسم المؤلف *"
                                    value={newBook.author}
                                    onChange={(e) => handleChange("author", e.target.value)}
                                    required
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                />
                                <input
                                    type="text"
                                    placeholder="المترجم (اختياري)"
                                    value={newBook.transl}
                                    onChange={(e) => handleChange("transl", e.target.value)}
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                />
                                <select
                                    value={newBook.type}
                                    onChange={(e) => handleChange("type", e.target.value)}
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                >
                                    <option value="عربي">عربي</option>
                                    <option value="مترجم">مترجم</option>
                                </select>
                                <select
                                    value={newBook.category}
                                    onChange={(e) => handleChange("category", e.target.value)}
                                    required
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                >
                                    <option value="">اختر التصنيف *</option>
                                    {FIXED_CATEGORIES.map(cat => (
                                        <option key={cat} value={cat}>{cat}</option>
                                    ))}
                                </select>
                                <input
                                    type="number"
                                    placeholder="السعر (ج.م) *"
                                    value={newBook.price}
                                    onChange={(e) => handleChange("price", e.target.value)}
                                    required
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                />
                                <input
                                    type="text"
                                    placeholder="حالة الورق (اختياري)"
                                    value={newBook.HPaper}
                                    onChange={(e) => handleChange("HPaper", e.target.value)}
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none"
                                />
                                <textarea
                                    placeholder="وصف الكتاب (اختياري)"
                                    rows="4"
                                    value={newBook.description}
                                    onChange={(e) => handleChange("description", e.target.value)}
                                    className="border border-gray-300 rounded-lg px-4 py-3 focus:border-blue-500 focus:outline-none md:col-span-2"
                                />
                                <div className="md:col-span-2">
                                    <label className="block mb-2 text-gray-700 font-medium">الصور الحالية (إذا كانت موجودة)</label>
                                    <div className="flex flex-wrap gap-2 mb-4">
                                        {editingBook?.images?.map((url, index) => (
                                            <div key={index} className="relative">
                                                <img src={url} alt="صورة الكتاب" className="w-24 h-32 object-cover rounded" />
                                                <button type="button" onClick={() => handleRemoveImage(index)} className="absolute top-0 right-0 bg-red-500 text-white p-1 rounded-full">
                                                    <FiX size={16} />
                                                </button>
                                            </div>
                                        ))}
                                    </div>
                                    <label className="block mb-2 text-gray-700 font-medium">إضافة صور جديدة (متعددة)</label>
                                    <input
                                        type="file"
                                        multiple
                                        accept="image/*"
                                        onChange={handleImageChange}
                                        className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                                    />
                                    <div className="flex flex-wrap gap-2 mt-4">
                                        {imagePreviews.map((preview, index) => (
                                            <div key={index} className="relative">
                                                <img src={preview} alt="معاينة" className="w-24 h-32 object-cover rounded" />
                                                <button type="button" onClick={() => handleRemoveImage(index)} className="absolute top-0 right-0 bg-red-500 text-white p-1 rounded-full">
                                                    <FiX size={16} />
                                                </button>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                                <div className="md:col-span-2 flex gap-4">
                                    <button type="submit" disabled={uploading} className="flex-1 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-medium flex items-center justify-center gap-2 disabled:opacity-50">
                                        {uploading ? <ClipLoader size={20} color="#fff" /> : null}
                                        {uploading ? "جاري الرفع..." : editingBook ? "حفظ التعديلات" : "إضافة الكتاب"}
                                    </button>
                                    <button type="button" onClick={() => { setShowAddForm(false); setEditingBook(null); resetForm(); }} className="flex-1 bg-gray-300 hover:bg-gray-400 text-gray-800 px-6 py-3 rounded-lg font-medium">
                                        إلغاء
                                    </button>
                                </div>
                            </form>
                        </motion.div>
                    )}
                </AnimatePresence>

                <input
                    type="text"
                    placeholder="ابحث بالعنوان أو المؤلف..."
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    className="w-full max-w-md mb-8 px-4 py-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none"
                />

                {loading ? (
                    <div className="text-center py-10">
                        <ClipLoader size={50} color="#3B82F6" />
                    </div>
                ) : filtered.length === 0 ? (
                    <p className="text-center text-gray-600 text-xl">لا توجد كتب</p>
                ) : (
                    <div className="space-y-6">
                        {filtered.map((book) => (
                            <motion.div
                                key={book.key}
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                className="bg-white p-6 rounded-xl shadow-md"
                            >
                                <div className="flex justify-between items-center mb-4">
                                    <h3 className="text-xl font-bold text-gray-800">{book.title}</h3>
                                    <div className="flex gap-3">
                                        <button onClick={() => startEditing(book)} className="text-blue-600 hover:text-blue-800">
                                            <FiEdit size={24} />
                                        </button>
                                        <button onClick={() => handleDelete(book)} className="text-red-600 hover:text-red-800">
                                            <FiTrash2 size={24} />
                                        </button>
                                    </div>
                                </div>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                                    <p><strong>المؤلف:</strong> {book.author}</p>
                                    <p><strong>المترجم:</strong> {book.transl || "غير محدد"}</p>
                                    <p><strong>النوع:</strong> {book.type}</p>
                                    <p><strong>التصنيف:</strong> {book.category}</p>
                                    <p><strong>السعر:</strong> {book.price} ج.م</p>
                                    <p><strong>حالة الورق:</strong> {book.HPaper || "غير محدد"}</p>
                                    <p><strong>الحالة:</strong> {book.status === "sold" ? "تم البيع" : "متاح"}</p>
                                </div>
                                <p className="mb-4"><strong>الوصف:</strong> {book.description || "غير محدد"}</p>
                                <div className="flex flex-wrap gap-2">
                                    {book.images?.map((url, index) => (
                                        <img key={index} src={url} alt="صورة الكتاب" className="w-20 h-28 object-cover rounded border border-gray-300" />
                                    ))}
                                </div>
                            </motion.div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}
